import { Component } from '@angular/core';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css'],
})
export class SearchComponent {
  serachText: string = 'Mens Wear';

  updateSearchText(event: any) {
    this.serachText = event.target.value;
  }
}
<div class="ekart--search--product">
  <input class="ekart-search-product-input" [(ngModel)] = "serachText">
  <button class="btn btn-search">Search</button>
</div>

<div class="ekart-search-result-for">
  <p><strong>Search result for : </strong>{{serachText}}</p>
</div>
